mapper.update:
update tb_rule_custom_interim
 set money=#{money}
 where car_type=#{car_type}
 and use_date=#{use_date}
 and rule_type=#{rule_type}

mapper.select:
        select count(*)
        from  tb_rule_custom_interim
        where car_type=#{car_type}
		 and use_date=#{use_date}
		 and rule_type=#{rule_type}

select *
from tb_rule_custom_interim
where true

    and rule_type =#{rule_type}


    and car_type =#{car_type}


    and money like CONCAT('%',#{money},'%')


    and TO_DAYS(use_date) = TO_DAYS(#{use_date})

order by interim_rule_id
limit #{pageNum} offset #{page};

select money
from tb_rule_custom_interim
where TO_DAYS(use_date) = TO_DAYS(#{date})
and rule_type=#{rule_type}
and car_type=#{car_type}
