mapper.insert:

insert into tb_parking_space (parking_space_id, parking_space_location,
parking_space_occupy, parking_space_question,
parking_space_reservation, car_id, expire_date
)
values (#{parkingSpaceId,jdbcType=INTEGER}, #{parkingSpaceLocation,jdbcType=VARCHAR},
#{parkingSpaceOccupy,jdbcType=INTEGER}, #{parkingSpaceQuestion,jdbcType=VARCHAR},
#{parkingSpaceReservation,jdbcType=INTEGER}, #{carId,jdbcType=INTEGER}, #{expireDate,jdbcType=TIMESTAMP}
)

mapper.delete:
DELETE FROM test.tb_parking_space
WHERE parking_space_id = #{parking_space_id}
and car_id !=null

mapper.select:
select *
from tb_parking_space
where true

    and parking_space_id =#{parking_space_key_word}
    or parking_space_location =#{parking_space_key_word}
    or car_id =#{parking_space_key_word}
    or TO_DAYS(expire_date) = TO_DAYS(#{parking_space_searchDate})


    and parking_space_id =#{parking_space_key_word}


    and parking_space_location =#{parking_space_key_word}


    and car_id =#{parking_space_key_word}


    and TO_DAYS(expire_date) = TO_DAYS(#{parking_space_searchDate})

order by parking_space_id

    limit #{pageNum} offset #{page};
